// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

error Unauthorized();

contract Marketplace {
    Course[] allCourses;
    address private immutable i_admin;

    constructor(address _admin) {
        i_admin = _admin;
    }

    struct Course {
        uint256 price;
        uint256 royaltyFee;
        uint256 yield;
        address creator;
        string title;
        string uri;
    }

    struct Specialization {
        Course[] courses;
        uint256 price;
        string title;
    }

    modifier onlyAdmin() {
        if (msg.sender != i_admin) {
            revert Unauthorized();
        }

        _;
    }

    /**
     * @notice Function allows an admin to create a course and adds that course to the list of courses
     * @dev
     * @param   price
     * @param   royaltyFee fee given to the creators of the course
     * @param   yield dividend paid out to the students
     * @param   creator .
     * @param   title .
     * @param   uri .
     */
    function createCourse(
        uint256 price,
        uint256 royaltyFee,
        uint256 yield,
        address creator,
        string memory title,
        string memory uri
    ) external onlyAdmin {
        Course memory course;
        course = Course(price, royaltyFee, yield, creator, title, uri);
        allCourses.push(course);
    }

    function updateCourse() external onlyAdmin {}
}

/*
    nft - imported
    struct - courses
    struct - specialization 
    enum - course state - used to give rewards
    import reentrancy gaurd from openzeppelin

    Functions 

    create specialization 
    update course 
    update specialization
    delete course
    delete specialization
    buy course - give royalties / mint and transfer nft 
    update state - when a course is completed and update nft uri
    dynamic yield - set yield dynamically
    update nft using chainlink automation / Automating payouts using chainlink automation

*/

